import React, { useState } from 'react';
import { BookOpen, Target, Users, Award } from 'lucide-react';
import LearnTab from './LearnTab';
import PracticeTab from './PracticeTab';
import CommunityTab from './CommunityTab';

const DomestikaCreativeAssistant = () => {
  const [activeTab, setActiveTab] = useState('learn');

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-6xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-gradient-to-r from-purple-600 to-pink-600 rounded-lg flex items-center justify-center text-white font-bold">
                D
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900">Domestika Creative Assistant</h1>
                <p className="text-sm text-gray-600">Your AI-powered creative companion</p>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2 text-sm text-gray-600">
                <Award className="w-4 h-4" />
                <span>Level 7 Creator</span>
              </div>
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full"></div>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white border-b border-gray-200">
        <div className="max-w-6xl mx-auto px-6">
          <div className="flex space-x-8">
            {[
              { id: 'learn', label: 'Learn', icon: <BookOpen className="w-5 h-5" /> },
              { id: 'practice', label: 'Practice', icon: <Target className="w-5 h-5" /> },
              { id: 'community', label: 'Community', icon: <Users className="w-5 h-5" /> }
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center gap-2 px-4 py-4 border-b-2 font-medium text-sm transition-colors ${
                  activeTab === tab.id
                    ? 'border-purple-600 text-purple-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700'
                }`}
              >
                {tab.icon}
                {tab.label}
              </button>
            ))}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-6xl mx-auto px-6 py-8">
        {activeTab === 'learn' && <LearnTab />}
        {activeTab === 'practice' && <PracticeTab />}
        {activeTab === 'community' && <CommunityTab />}
      </main>
    </div>
  );
};

export default DomestikaCreativeAssistant;
